import React from "react";
import Navbar from "../Component/Navbar";
import { DataGrid } from "@mui/x-data-grid";
import Paper from "@mui/material/Paper";
export default function Home() {
  const columns = [
    { field: "id", headerName: "ID", width: 250 },
    { field: "Username", headerName: "Username", width: 400 },
    {
      field: "type",
      headerName: "ประเภทเงินเดือน (เงินเดือน / ตั๋ว)",
      width: 400,
    },
  ];

  const rows = [
    { id: 1, Username: "Snow", type: "เงินเดือน" },
    { id: 2, Username: "Lannister", type: "เงินเดือน" },
    { id: 3, Username: "Lannister", type: "เงินเดือน" },
    { id: 4, Username: "Stark", type: "ตั๋ว" },
    { id: 5, Username: "Targaryen", type: "เงินเดือน" },
    { id: 6, Username: "Melisandre", type: "ตั๋ว" },
    { id: 7, Username: "Clifford", type: "เงินเดือน" },
    { id: 8, Username: "Frances", type: "ตั๋ว" },
    { id: 9, Username: "Roxie", type: "ตั๋ว" },
  ];

  const paginationModel = { page: 0, pageSize: 5 };

  return (
    <div className="w-full h-screen bg-gradient-to-r from-black via-green-900 to-black">
      <Navbar />
      <div className="w-full h-20 flex justify-between text-white items-center">
        <div className="w-[50%] text-center">
          <button className="TITLENAV" data-text="Awesome">
            <span className="actual-text poppins-bold">
              &nbsp;USERALL&nbsp;
            </span>
            <span aria-hidden="true" className="hover-text poppins-bold">
              &nbsp;USERALL&nbsp;
            </span>
          </button>
        </div>
        <div className="w-[50%]"></div>
      </div>
      <div className="w-full h-[70%] flex items-center justify-center">
        <div className="w-[80%] h-[100%] bg-black/40 flex items-center">
        <Paper
  sx={{ backgroundColor: "transparent", height: 400, width: "100%" }}
>
  <DataGrid
    rows={rows}
    columns={columns}
    initialState={{ pagination: { paginationModel } }}
    pageSizeOptions={[5, 10]}
    sx={{
      border: 0, // ไม่มีขอบ
      backgroundColor: "transparent", // ไม่มีสีพื้นหลัง
      color: "#fff", // ตัวหนังสือสีขาว
      "& .MuiDataGrid-cell": {
        color: "#fff", // ตัวหนังสือเซลล์สีขาว
        backgroundColor: "transparent !important", // ไม่มีพื้นหลังในเซลล์
      },
      "& .MuiDataGrid-columnHeaders": {
        backgroundColor: "rgba(0, 0, 0, 0) !important", // ไม่มีพื้นหลังในส่วนหัวคอลัมน์
        color: "#fff !important", // ตัวหนังสือส่วนหัวสีขาว
        borderBottom: "1px solid rgba(255, 255, 255, 0.2)", // เส้นใต้ส่วนหัวคอลัมน์ (ถ้าต้องการ)
      },
      "& .MuiDataGrid-columnHeaderTitle": {
        color: "#fff !important", // ตัวหนังสือในส่วน Header
        fontWeight: "bold", // ตัวหนังสือหนา
      },
      "& .MuiDataGrid-columnSeparator": {
        display: "none !important", // ซ่อนเส้นแบ่งคอลัมน์
      },
      "& .MuiDataGrid-footerContainer": {
        backgroundColor: "transparent !important", // ไม่มีพื้นหลัง Footer
        color: "#fff !important", // ตัวหนังสือ Footer สีขาว
      },
      "& .MuiDataGrid-row": {
        backgroundColor: "transparent !important", // ไม่มีพื้นหลังในแถว
      },
      "& .MuiDataGrid-row:hover": {
        backgroundColor: "rgba(255, 255, 255, 0.1) !important", // เพิ่มสีเล็กน้อยเมื่อ Hover
      },
      "& .MuiTablePagination-root": {
        backgroundColor: "transparent !important", // ไม่มีพื้นหลัง Pagination
        color: "#fff !important", // ตัวหนังสือ Pagination สีขาว
      },
      "& .MuiDataGrid-virtualScroller": {
        backgroundColor: "transparent !important", // ไม่มีพื้นหลังใน Scroll
      },
    }}
  />
</Paper>

        </div>
      </div>
    </div>
  );
}
